import axios from "axios";
import { useState, useEffect } from "react";
import { Link as NavLink } from "react-router-dom";
import {
  Pagination,
  PaginationItem,
  TextField,
  Stack,
  Link
} from "@mui/material";

const BASE_URL = "https://api.github.com/search/repositories?";

const HomePage = (props) => {
  console.log(props);
  const [repo, setRepo] = useState([]);
    const [query, setQuery] = useState("");
    const[perPage,setPerPage]=useState()
  const [currentPage, setCurrentPage] = useState(
    parseInt(props.location.search?.split("=")[1] || 1)
  );
  const [count, setCount] = useState(0);

  useEffect(() => {
      axios.get(BASE_URL + `q={repo}&per_page=${perPage}&page=${currentPage}`).then(({ data }) => {
        console.log(data)
      setRepo(data.hits);
      setCount(data.nbPages);

    //   if (data.nbPages < currentPage) {
    //     setCurrentPage(1);
    //     props.history.replace("/");
    //   }
    });
  }, [query, currentPage]);

  return (
    <>
      <TextField
        fullWidth
        label="query"
        value={query}
        onChange={(event) => setQuery(event.target.value)}
      />
      <Stack spacing={2}>
        {!!pageQty && (
          <Pagination
            count={count}
            page={currentPage}
            onChange={(_, num) => setCurrentPage(num)}
            showFirstButton
            showLastButton
            sx={{ marginY: 3, marginX: "auto" }}
            renderItem={(item) => (
              <PaginationItem
                component={NavLink}
                to={`/?page=${item.page}`}
                {...item}
              />
            )}
          />
        )}
        {repo.map((post) => (
          <Link key={post.objectID} href={post.url}>
            {/* {post.title || post.story_title} */}
          </Link>
        ))}
      </Stack>
    </>
  );
};

export default HomePage;
