import ReactDOM from 'react-dom';
import { toast, ToastContainer, Zoom } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import Button from '@mui/material/Button';
import s from './Form.module.css'
import React, { useState } from 'react';
import List from '../List/List'
import { useDispatch } from 'react-redux';
import * as actions from '../../redux/users/users-action'
import { formControlUnstyledClasses } from '@mui/base';
import { useSelector} from "react-redux";
import * as selectors from '../../redux/users/user-selector'
import { Pagination } from '@mui/material';
import HomePage from '../Pagination/Pagination';
import HandlePagination from '../Pagination/Pagination';


export default function Form() {
  const [data, setData] = useState({});
  const [repo, setRepo] = useState("");
  const dispatch = useDispatch();
    const perPage = 20;
  const [currentPage, setCurrentPage] = useState('')
    // parseInt(props.location.search?.split("=")[1] || 1)
  const [count, setCount] = useState(0);



  //   const onChangeHandler = e => {
  //   setRepo(e.target.value);
  // };

  // const submitHandler = async e => {
  //   e.preventDefault();
    useEffect(() => {
    const profile =  fetch(`https://api.github.com/search/repositories?q=${repo}&per_page=${perPage}&page=${currentPage}`);
    const profileJson= profile.json();
    setCount(profileJson.total_count)
    if (!repo) return toast.error('Please input repositories');
    if (!repo.trim()) return toast.error('Please input repositories');
    if (profileJson.items.length === 0) return toast.warning('Please input repositories');
    if (profileJson) {
      dispatch(actions.fetchRepositoriesSuccess(profileJson))
    }



    //   if (data.nbPages < currentPage) {
    //     setCurrentPage(1);
    //     props.history.replace("/");
    //   }

  }, [repo, currentPage,perPage]);



    return (
    <>
        <form className={s.form} >
            <label className={s.label}>
                Name
                <input
                        type="text"
                        placeholder="input GitHub user name"
                    name="name"
                    pattern="^[a-zA-Zа-яА-Я]+(([' -][a-zA-Zа-яА-Я ])?[a-zA-Zа-яА-Я]*)*$"
                    title="Имя может состоять только из букв, апострофа, тире и пробелов. Например Adrian, Jacob Mercer, Charles de Batz de Castelmore d'Artagnan и т. п."
                    required
                    onChange={onChangeHandler}
                    value={repo}
                    className={s.input} />
                </label>
                <Button  onClick={submitHandler} type="submit">Search</Button>
            </form>
        <List />


            <ToastContainer transition={Zoom} autoClose={3000} />

            </>
) 
};
