import { createAction, createReducer } from "@reduxjs/toolkit";
import {addRepositoriesSuccess,deleteRepositoriesSuccess,fetchRepositoriesSuccess, setCurrentPage,clearCurrentPage} from './users-action';
import { combineReducers } from "redux";

const initialState = {
    perPage: 10,
    currentPage: 1,
    totalCount:500,

};


const items = createReducer([],{
    [fetchRepositoriesSuccess]: (_, { payload }) => [...payload]
    ,
    [deleteRepositoriesSuccess]: (state, {payload}) =>
    state.filter(({id} ) => id !== payload),
    [addRepositoriesSuccess]: (state, { payload }) => [payload, ...state],
});
const currentPage = createReducer(initialState, {
    [setCurrentPage]: (_, { payload }) => payload,
    [clearCurrentPage]:()=> initialState,

})


export default combineReducers({
    items,
    currentPage,

})
