import { useParams } from 'react-router-dom';
import { useState, useEffect } from 'react';
import s from './RepoCard.module.css'

export default function RepoCard() {
  const [repo, setRepo] = useState(null);
  const { id } = useParams();

  useEffect(() => {
    (async () => {
      const fetchRepo = await fetch(
        `https://api.github.com/repositories/${id}`
      );
      if (fetchRepo) {
        const repoJson = await fetchRepo.json();
        setRepo(repoJson);
      }
    })();
  }, [id]);
  return (
    <>
          {repo && (

        <div className={s.container}>
          <h1> Name :{repo.name}</h1>
          <img src={repo.owner.avatar_url} alt={repo.owner.login}></img>
          <p>Login :{repo.owner.login}</p>
          <a rel="noopener noreferrer" target="_blank" href={repo.owner.url}>link </a>
            <p>{repo.owner.login}</p>
            <p>{repo.html_url}</p>
                                    <p>DeScription:{ repo.description}</p>
            <p>Date:{ repo.created_at.split('T').shift()}</p>

        </div>
      )}
    </>
  );
}
