import { useState,useEffect } from "react";
export default function MoreInfo() {
    const [username, setUsername] = useState("");
    const [repo, setRepo] = useState({});
    const { id, login, name } = useParams();

    const fetchRepoById= async e => {

        const fetchRepo = await fetch(`https://api.github.com/repositories/${id}`);
        const repoJson = await fetchRepo.json();
        console.log(repoJson)
        setRepo(repoJson)
    }
    useEffect(() => {
        fetchRepoById()

    }, [id])
    return (
        <>
            <h1>{repo.name}</h1>
            <img src={repo.owner.avatar_url} alt={repo.owner.login}></img>
            <p>{repo.owner.login}</p>
            <a href={repo.owner.url}>link</a>

            <p>{repo.owner.login}</p>



</>
    )

};
