import ReactDOM from 'react-dom';
import { toast, ToastContainer, Zoom } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import Button from '@mui/material/Button';
import s from './Form.module.css'
import React, { useState,useEffect } from 'react';
import List from '../List/List'
import { useDispatch } from 'react-redux';
import * as actions from '../../redux/users/users-action'
import { formControlUnstyledClasses } from '@mui/base';
import { useSelector} from "react-redux";
import * as selectors from '../../redux/users/user-selector'
import HomePage from '../Pagination/Pagination';
import{link as NavLink} from 'react-router-dom'
import {
  Pagination,
  PaginationItem,
  Stack,
  Link
} from "@mui/material";


export default function Form() {

  const pages = useSelector(selectors.getState);
  // const [data, setData] = useState({});
  const [repo, setRepo] = useState("");
  const dispatch = useDispatch();
  const perPage = 12;
  // parseInt(props.location.search?.split("=")[1] || 1)
  const [currentPage, setCurrentPage] = useState(1)
    // parseInt(props.location.search?.split("=")[1] || 1)
  const [count, setCount] = useState(0);
  const onChangeHandler = e => {
    setRepo(e.target.value);
  };
  useEffect(() => {
  if (!repo)
            return
    getData(count/perPage)  

  }, [currentPage])

  const getData = async () => {
    const profile = await fetch(`https://api.github.com/search/repositories?q=${repo}&per_page=${perPage}&page=${currentPage}`);
    const profileJson = await profile.json();
    setCount(profileJson.total_count)
    if (!repo) return toast.error('Please input repositories');
    if (!repo.trim()) return toast.error('Please input repositories');
    if (profileJson.items.length === 0) return toast.warning('Please input repositories');
    if (profileJson) {
      dispatch(actions.fetchRepositoriesSuccess(profileJson))
      dispatch(actions.setCurrentPage(profileJson))
    }
  }
  const submitHandler = (e) => {
    e.preventDefault();
    getData();
  }
  return (
    <>
        <form className={s.form} onSubmit={(e)=>submitHandler(e)}>
            <label className={s.label}>
                Name
                <input
                    type="text"
                    placeholder="input GitHub user name"
                    name="name"
                    pattern="^[a-zA-Zа-яА-Я]+(([' -][a-zA-Zа-яА-Я ])?[a-zA-Zа-яА-Я]*)*$"
                    title="Имя может состоять только из букв, апострофа, тире и пробелов. Например Adrian, Jacob Mercer, Charles de Batz de Castelmore d'Artagnan и т. п."
                    required
              onChange={onChangeHandler}
                    value={repo}
                    className={s.input} />
                </label>
                <Button  type="submit">Search</Button>
        </form>
    <List />
      <Stack spacing={2}>
        {!!count && pages && (
          <Pagination
          count={Math.ceil(count/perPage)}
          page={currentPage}
          onChange={(_, num) => setCurrentPage(num)}
          showFirstButton
          showLastButton
          sx={{ marginY: 3, marginX: "auto" }}
          renderItem={(item) => (
            <PaginationItem
            component={NavLink}
            to={`/?page=${item.page}`}
            {...item}
            />
            )}
            />
            )}
          </Stack>



            <ToastContainer transition={Zoom} autoClose={3000} />

            </>
) 
};
