import { Link } from "react-router-dom";
import { useSelector} from "react-redux";
import * as selectors from '../../redux/users/user-selector'
import s from './ListItem.module.css'
import FavoriteCardButton from "../FavoriteCardButton";
import { useState } from "react";



export default function ListItem() {
    // const { repo,owner} = useSelector(({ repositories }) => repositories);
    const [click, setClick] = useState('false')
    const repo = useSelector(selectors.getState);

    return (
        <li key={repo.id} className={ s.gallery__list__elem} >
                <div className={s.img__thumb}>
                {!click ? <FavoriteCardButton/>:<></>}
                <img className={s.img} src={repo.owner.avatar_url} alt='dfsdf' />
                </div>
            <p className={s.name}>Repository name -- {repo.name}</p>
            <p className={s.name}>Repository rating --{repo.stargazers_count}</p>
            <div className={s.cont}>
            <Link  className={s.button} to={`/search/${repo.id}`}>View More</Link>
            <button className={s.button} onClick={() =>setClick(!click) } type='button'>Add to favorite</button>

            </div>
        </li>)

};
