import React, { useState, useEffect } from 'react';


export default function MainPage() {
    const [isLoading, setIsLoading] = useState(true);

useEffect(() => {

	// Wait for 3 seconds
	setTimeout(() => {
	setIsLoading(false);
	}, 3000);
}, []);

// Custom css for loader
const override = `
display: block;
margin: 0 auto;
border-color: red;
`;

return isLoading ?

	// If page is still loading then splash screen

	<h1 className="App">
	This is Main Page
    </h1> :<></>

};
