import * as actions from '../../redux/users/users-action'
import { useSelector} from "react-redux";

import * as selectors from '../../redux/users/user-selector'

import { formControlUnstyledClasses } from '@mui/base';

import getRepos from '../services/api';


export default function Form() {
  const [repo, setRepo] = useState("");
  const dispatch = useDispatch();
  const currentPage = useSelector(selectors.getPage)
  const totalCount = useSelector(selectors.getPage)
  const perPage = useSelector(selectors.getPage)



